import React, { Component } from 'react';
import {
  BrowserRouter,
  Route,
  Switch,
  Redirect
} from 'react-router-dom';
import axios from 'axios';
import './App.css';
import apiKey from './config.js';

//import components
import Nav from './Components/Nav';
import Search from './Components/Search';
import Gallery from './Components/Gallery';
import NoResults from './Components/NoResults';
import NotFound from './Components/NotFound';

export default class App extends Component {
  state = {
    pictures: [],
    mountain: [],
    forest: [],
    beach: [],
    loading: true
  };

  componentDidMount(props) {
    this.performSearch();
  }

  performSearch = (query) => {
    axios.get(`https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=${apiKey}&tags=${query}&per_page=24&format=json&nojsoncallback=1`)
      .then(response => {
        this.setState({
          pictures: response.data.photos.photo,
          loading: false
        })
      })
      .catch(error => {
        console.log("Error fetching data", error);
      })

    axios.get(`https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=${apiKey}&tags=mountain&per_page=24&format=json&nojsoncallback=1`)
      .then(response => {
        this.setState({
          mountain: response.data.photos.photo,
          loading: false
        })
      })
      .catch(error => {
        console.log("Error fetching data", error);
      })

    axios.get(`https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=${apiKey}&tags=forest&per_page=24&format=json&nojsoncallback=1`)
      .then(response => {
        this.setState({
          forest: response.data.photos.photo,
          loading: false
        })
      })
      .catch(error => {
        console.log("Error fetching data", error);
      })

    axios.get(`https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=${apiKey}&tags=beach&per_page=24&format=json&nojsoncallback=1`)
      .then(response => {
        this.setState({
          beach: response.data.photos.photo,
          loading: false
        })
      })
      .catch(error => {
        console.log("Error fetching data", error);
      })
  }

  render() {
    console.log(this.state.pictures);
    return (
      <BrowserRouter>
        <div className="container">
          <Search onSearch={this.performSearch} />
          <Nav />
          <Switch>
            <Route exact path="/" render={() => <Redirect to="/mountain" />} />
            <Route path="/mountain" render={() => <Gallery data={this.state.mountain} onSearch={this.performSearch} loading={this.state.loading} />} />
            <Route path="/forest" render={() => <Gallery data={this.state.forest} onSearch={this.performSearch} loading={this.state.loading} />} />
            <Route path="/beach" render={() => <Gallery data={this.state.beach} onSearch={this.performSearch} loading={this.state.loading} />} />
            <Route path="/search/:query" render={(props) => <Gallery data={this.state.pictures} onSearch={this.performSearch} loading={this.state.loading} {...props} />} />
            <Route component={NotFound} />
          </Switch>

          {/* <Route
              path='/search/:name' render={props => <Gallery data={this.state.photos} {...props} />
                )
              }
            /> */}


          {/* {
            (this.state.loading)
              ? <p>Loading...</p>
              : <Gallery data={this.state.pictures} />
          } */}

        </div>
      </BrowserRouter>
    )
  }
};
